const editorConfig = {
  placeholder: '请输入内容...',
  MENU_CONF: {
    'uploadImage': {
      // 最大图片限制
      maxFileSize: 1024 * 1024 * 20, // 20M
      // 自己处理上传图片
      customUpload(file:any, insertFn: any) {
        console.log(file, 'customUpload')
        // file 即选中的文件
        // 自己实现上传，并得到图片 url alt href
        // 最后插入图片
        // insertFn(url, alt, href)
      },
      // 小于该值就插入 base64 格式（而不上传），默认为 0
      base64LimitSize: 1024 * 1024 * 30 // 30M
    }
  }
}

